import { useState, useEffect } from 'react'

import Filter from './components/Filter'
import People from './components/People'
import PersonForm from './components/PersonForm'
import personServices from './services/people'
import Notification from './components/Notification'

const App = () => {
  const [people, setPerson] = useState([])
  const [newName, setNewName] = useState('')
  const [newNumber, setNewNumber] = useState('')
  const [newSearch, setNewSearch] = useState('')
  const [succesMessage, setSuccesMessage] = useState('')

  useEffect(() => {
    personServices
      .getAll()
      .then(initialPeople => {
        setPerson(initialPeople)
      })
  }, [])

  const getId = () => {
    const arr = new Array(people.length)

    for (let i = 0; i < arr.length; i++) {
      arr[i] = i + 1;
    }

    console.log(arr)

    for (let i = 0; i < arr.length; i++) {
      for (let j = 0; j < people.length; j++) {
        if (arr[i] === people[j].id) {
          arr.shift()
          break
        } 
      }
    }

    if (arr.length) {
      return arr[0]
    } else {
      return people.length + 1
    }
  } 

  const addPerson = (event) => {
    event.preventDefault()
    const person = people.find(p => p.name.toLowerCase() === newName.toLowerCase())

    if (person && window.confirm(`${newName} is already added to phonebook, replace the old number with a new one?`)) {
      const changedPerson = { ...person, number: newNumber }

      personServices
        .update(changedPerson.id, changedPerson)
        .then(returnedPerson => {
          setPerson(people.map(person => person.id !== changedPerson.id ? person : changedPerson))

          setSuccesMessage(
            `Updated ${newName}'s phone number` 
          )
          setTimeout(() => {
            setSuccesMessage(null)
          }, 5000);
        })

    } else {
      const newPerson = {
        name: newName,
        number: newNumber,
        id: getId()
      }

      console.log(newPerson.id)

      personServices
        .create(newPerson)
        .then(response => {
          setPerson(people.concat(newPerson))
          setSuccesMessage(
            `Added ${newName}` 
          )
          setTimeout(() => {
            setSuccesMessage(null)
          }, 5000);
        })
    }

    setNewName('')
    setNewNumber('')
  }

  const handleNameChange = (event) => {
    console.log(event.target.value)
    setNewName(event.target.value)
  }

  const handleNumberChange = (event) => {
    console.log(event.target.value)
    setNewNumber(event.target.value)
  }

  const handleSearchChange = (event) => {
    console.log(event.target.value)
    setNewSearch(event.target.value)
  }

  const handlePersonDelete = (id) => {
    console.log(id)

    if (window.confirm(`Delete ${people[id - 1].name}?`)) {
      personServices
        .deletePerson(id)
        .then(response => {
          personServices
            .getAll()
            .then(initialPeople => {
              setPerson(initialPeople)
            })
        })
    }
  }

  const peopleToShow = people.filter(person => 
    person.name.toLowerCase().includes(newSearch.toLowerCase())  
  )

  return (
    <div>
      <h1>Phonebook</h1>
      <Notification message={succesMessage}/>
      <Filter 
        newSearch={newSearch}
        handleSearchChange={handleSearchChange}
      />
      <h2>add a new</h2>
      <PersonForm 
        addPerson={addPerson}
        newName={newName}
        newNumber={newNumber}
        handleNameChange={handleNameChange}
        handleNumberChange={handleNumberChange}
      />
      <h2>Numbers</h2>
      <People 
        peopleToShow={peopleToShow} 
        peopleDeleter={handlePersonDelete}
      />
    </div>
  )
}

export default App
